({
  run: () => {
    console.log('Plugin123 is running')
    ethereumProvider.onNewTx(txMeta => {
      let state = ethereumProvider.getPluginState()
      ethereumProvider.updatePluginState({
        ...state,
        [txMeta.txParams.from]: (state[txMeta.txParams.from] || 0) + 1,
      })
      state = ethereumProvider.getPluginState()
      console.log('Number of transactions created by address', JSON.stringify(state, null, 2));
      ethereumProvider.setAccountLabel(txMeta.txParams.from, `Created ${state[txMeta.txParams.from]} tx`)
    })
  }
})
